import React from 'react'
import { useState, useEffect} from 'react'
import { Col, Container, Row, Table, Button, NavLink} from 'react-bootstrap'
import { Form } from 'react-bootstrap'
import axios from 'axios'

export const CreateListing = () => {

    const[dataLoaded, setDataLoaded] = useState(false)
    const [books, setBooks] = useState()
    const [selectedBook, setSelectedBook] = useState();

    const populateData = async () => 
    {
        try 
        {
            const responseGetData = await axios.get('http://localhost:8081/api/books/getallbooks');
            return responseGetData.data;

        }   
        catch(error)
        {
          console.log(error);
        }
    }

    useEffect(() => {
        async function fetchData(){
            if(dataLoaded === false){
                const booksData = await populateData()
                console.log("BOO", booksData)
                setBooks(booksData)
                setDataLoaded(true)
            }
        }
        fetchData()

    }, [dataLoaded])

    const booksList = books.map(book => {
        return (
            <option value = {book.id}>{book.title}</option>
        )
    })
    return (
        <div>
            <Container>
                <Form>
                <Form.Group>
                        <Form.Label>Book</Form.Label>

                        <Form.Label
                            className="me-sm-2"
                            htmlFor="inlineFormCustomSelect"
                            visuallyHidden
                            >
                            Select Book
                        </Form.Label>
                        <Form.Control as ="select" name='book' value={selectedBook} onChange={e=> {
                            setSelectedBook(e.target.value);
                            console.log(e.target.value);
                        }}>
                            {booksList}
                        </Form.Control>
                        <br></br>

                    </Form.Group>
                </Form>
            </Container>

        </div>
    )
}
