import React from 'react'
import { useState, useEffect} from 'react'
import { Col, Container, Row, Table, Button} from 'react-bootstrap'
import { NavLink } from 'react-router-dom'
import axios from 'axios'
import './MyListings.css'
import { useSessionUser } from '../../Hooks/useSessionUser'

export const MyListings = () => {
    const {sessionUser} = useSessionUser();
    const[dataLoaded,setDataLoaded] = useState(false);
    const[listingsData, setListingsData] = useState(null);
    const[bookData, setBookData] = useState(null);
    const[completeData, setCompleteData] = useState({
        bookId:null,
        title:null,
        author:null,
        type:null,
        price:null,
        sold:null
    });

    const getListingsContents = async () =>
    {
        try
        {
            const config = 
            {
              headers: {
                  Accept: 'application/json',
                  'Content-Type': 'application/json',
                  'Access-Control-Allow-Origin': '*',
              },

            }

            const sellerRequest = 
            {
                seller:String(sessionUser.username)
            }

            console.log("CUNT")
            const response = await axios.post('http://localhost:8082/api/listings/getlistingsbyseller', sellerRequest, config);

            console.log("Listings response", response);

            if (response.status === 200)
            {
                console.log(response.status)
                return response.data;
            }
            else
            {
                console.log("FUCK")
            }
        }
        catch (error)
        {

        }
    }

    const getBookContents= async () =>
    {
        try
        {
            const response = await axios.get('http://localhost:8081/api/books/getallbooks');

            return response.data;
        }
        catch (error)
        {

        }
    }

    useEffect(()=>
    {
        async function fetchData() {
            console.log("GOTCHA BITCH", dataLoaded)
            if (dataLoaded === false)
            {
                const listingsDataFetched = await getListingsContents();
                const bookDataFetched = await getBookContents();
                console.log("Listings fetched", listingsDataFetched);
                console.log("Books fetched", bookDataFetched);

                if (listingsDataFetched && bookDataFetched)
                {
                    if (!listingsDataFetched.results && !bookDataFetched.results)
                    {
                        setListingsData(listingsDataFetched);
                        setBookData(bookDataFetched);
                        setDataLoaded(true);

                        // const combined = Object.assign(listingsData, bookData);
                        // setCompleteData({
                        //     bookId:bookData.id,
                        //     title:bookData.title,
                        //     author:bookData.author,
                        //     type:listingsData.type,
                        //     price:listingsData.price,
                        //     sold:listingsData.sold
                        // });
                        // console.log("HOWDY NEIGHBOUR")

                        console.log("Combined Data", completeData)
                    }   
                }
                else
                {
                    setListingsData();
                    setBookData();
                    setDataLoaded(true);
                }
            }
        }
        fetchData();
    }, [listingsData, bookData])

    if(listingsData && bookData)
    {
        console.log("listings data", listingsData)
        console.log("book data", bookData)
        const allData = completeData.map(listItem => {
            return (

                <tr>
                <td>{listItem.bookId}</td>
                <td>{listItem.title}</td>
                <td>{listItem.author}</td>
                <td>{listItem.type}</td>
                <td>{listItem.price}</td>
                <td>{listItem.sold}</td>
                </tr>
            )
        })

        return(
            <div>
            <Container>
                <Row>
                    <Col>
                        <div className='addListing'>
                            <NavLink to = "/create-listing">
                            <Button variant ='success' style={{width:'100px'}}>
                                Add
                            </Button>
                            </NavLink>
                        </div>
                    </Col>
                </Row>
            </Container>

            <Container>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>Book ID</th>
                        <th>Title</th>
                        <th>Author</th>
                        <th>Type</th>
                        <th>Price</th>
                        <th>Status</th>


                        </tr>
                    </thead>
                    <tbody>
                        {allData}
                    </tbody>

                </Table>

            </Container>


            </div>
        )
    }
    else{
        return (
            <div>
                <Container>
                    <h1>
                        My Listings
                    </h1>
                </Container>
                <Container>

                    <Row>
                        <Col>
                            <div className='addListing'>
                                <NavLink to = "/create-listing">
                                    <Button variant ='success' style={{width:'100px'}}>
                                        Add
                                    </Button>
                                </NavLink>
                            </div>
                        </Col>
                    </Row>
                </Container>
                <Container>
                    <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>Book ID</th>
                        <th>Title</th>
                        <th>Author</th>
                        <th>Type</th>
                        <th>Price</th>
                        <th>Status</th>


                        </tr>
                    </thead>
                    <tbody>

                    </tbody>

                </Table>
                </Container>
            </div>

        )
    }
}
