import React from 'react'
import { useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router';
import { useEffect } from 'react';

export const BookListing = ({bookID}) => {
    const [tableData, setTableData] = useState([]);
    const [book, setBook] = useState({id: 0});
    const [dataLoaded, setLoadedData] = useState(false);
    const [listings, setListings] = useState();

    const populateData = async (request) =>
    {
        try
        {
            const config = 
            {
              headers: {
                  Accept: 'application/json',
                  'Content-Type': 'application/json',
                  'Access-Control-Allow-Origin': '*',
              },
              withCredential: true
            }

            if (request === "getBook")
            {
                const bookRequest = {
                    id:parseInt(bookID)
                }

                console.log("Sending book request")
                const response = await axios.post("http://localhost:8081/api/books/getbook", bookRequest, config);

                return response.data;
            }

            else if (request === "getListings")
            {
                const listingRequest = {
                    bookId:parseInt(bookID)
                }

                console.log("Sending listings request")
                const response = await axios.post("http://localhost:8082/api/listings/getlistingsbybookid", listingRequest, config);
                console.log("Got listings", response.data)
                return response.data;
            }

        }  catch(error)
        {
            console.log(error)
        }
    }

    useEffect(() => {
        async function fetchData(){
            if (dataLoaded === false) {
                const bookData = await populateData("getBook");
                const listingsData = await populateData("getListings");

                setBook(bookData);
                setLoadedData(true);

                if (!listingsData.results)
                {
                    setListings(listingsData);
                }

                if (listingsData && bookData)
                {
                    if (!listingsData.results && !bookData.results)
                    {
                        let data = []
                        // setListingsData(listingsDataFetched);
                        // setBookData(bookDataFetched);
                        for(const listing of listingsData){
                            let listingItem = {
                                id:listing.id,
                                type:listing.type,
                                price:listing.price,
                                sold:listing.sold

                            }
                            for(const book of bookData){
                                if(listing.bookId === book.id){
                                    listingItem = {
                                        ...listingItem, 
                                        bookId:book.id,
                                        title:book.title,
                                        author:book.author
                                    }
                                    data.push(listingItem)
                                }
                            }
                        }

                        console.log("Combined", data);
                        setTableData(data);  
                    }   
                }
            }
        }
        fetchData();

    }, [dataLoaded, book, listings])
    if (tableData)
    {
        const displayTableData = tableData.map(listItem => {
            return (

                <tr>
                <td>{listItem.id}</td>
                <td>{listItem.title}</td>
                <td>{listItem.author}</td>
                <td>{listItem.type}</td>
                <td>{listItem.price}</td>
                {
                    listItem.sold
                    ?
                    (<div>
                        Sold
                    </div>)
                    :
                    (<div>
                        Not sold
                    </div>
                    )
                }
                </tr>
            )
        })
    }
        return (
            <div>
                {tableData}
            </div>
        )
    }
