import React from 'react'
import { Container } from 'react-bootstrap'
import { useState, useEffect} from 'react';
import { NavLink, useParams } from 'react-router-dom';
import { Image } from 'react-bootstrap';
import { Row, Col, Button} from 'react-bootstrap'
import './Book.css';

const axios = require('axios');

export const Book = () => {

    const ColouredLine = ({color}) => (
        <hr
            style={{
                color:color,
                backgroundColor: color,
                height: 2
            }}
        />
    );

    const [book, setBook] = useState({id: 0});
    const [dataLoaded, setLoadedData] = useState(false);
    let {bookID} = useParams();

    const populateData = async (url, type) =>
    {
        try
        {
            const config = 
            {
              headers: {
                  Accept: 'application/json',
                  'Content-Type': 'application/json',
                  'Access-Control-Allow-Origin': '*',
              },
              withCredential: true
            }

            const bookRequest = {
                type:parseInt(bookID)
            }

            console.log("Sending request")
            const response = await axios.post(url, bookRequest, config);

