    }, [dataLoaded, book])

    if (book)
    {
        return (
            <div>
                <Container>
                    <Row>
                        <div className='layout'>
                            <div className='imageContainer'>
                            <Image fluid src= {book.imgURL}>
                            </Image>
                            </div>

                            <div className='content'>
                                <div className='title'>
                                    {book.title}
                                </div>

                                <div className='author'>
                                    By: {book.author}
                                </div>

                                <ColouredLine color='black'/>
                                <span>
                                    <strong>
                                        ISBN: 
                                    </strong>
                                </span>{' '}
                                {book.isbn}
                                <br/>

                                    <strong>
                                        Description: 
