import React from 'react'
import { useState, useEffect} from 'react'
import { Col, Container, Row, Table, Button} from 'react-bootstrap'
import { NavLink } from 'react-router-dom'
import axios from 'axios'
import './MyListings.css'

export const MyListings = () => {
    const[dataLoaded,setDataLoaded] = useState(false);
    const[data, setData] = useState();
    const[bookData, setBookData] = useState();
    const[completeData, setCompleteData] = useState({
        id:"",
        title:"",
        author:"",
        type:"",
        price:"",
        sold:""
    });

    const getTableContents = async () =>
    {
        try
        {
            const response = await axios.get('http://localhost:8082/api/listings/getlistingsbyseller');

            return response.data;
        }
        catch (error)
        {

        }
    }

    const getBookContents= async () =>
    {
        try
        {
            const response = await axios.get('http://localhost:8081/api/books/getbook');

            return response.data;
        }
        catch (error)
        {

        }
    }

    useEffect(()=>
    {
        async function fetchData() {
            if (dataLoaded === false)
            {
                const listingsData = await getTableContents();
                const bookData = await getBookContents();
                console.log(listingsData);
                console.log(bookData);

                setData(listingsData);
                setBookData(bookData);
                setCompeleteData({

                })

                setDataLoaded(true);
            }
        }
        fetchData();
    }, [dataLoaded, data])


    const display = {
        listingbookId:data.bookId,

    }


    if(data && bookData){
        const allListingsData = data.map(listing => {
            return (

                <tr>
                <td>{listing.bookId}</td>
                <td>{listing.type}</td>
                <td>{listing.price}</td>
                <td>{listing.sold}</td>

                </tr>
            )
        })

        const allBookData = bookData.map(book => {
            return (
                <tr>
                <td>{book.title}</td>
                <td>{book.author}</td>

                </tr>
            )
        })
        return(
            <div>
            <Container>
                <Row>
                    <Col>
                        <div className='addListing'>
                            <NavLink to = "/create-listing">
                            <Button variant ='success' style={{width:'100px'}}>
                                Add
                            </Button>
                            </NavLink>
                        </div>
                    </Col>
                </Row>
            </Container>

            <Container>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>Book ID</th>
                        <th>Title</th>
                        <th>Author</th>
                        <th>Type</th>
                        <th>Price</th>
                        <th>Status</th>


                        </tr>
                    </thead>
                    <tbody>
                        {allListingsData}
                    </tbody>

                </Table>

            </Container>


            </div>
        )
    }
    else{
        return (
            <div>
                <Container>
                    <h1>
                        My Listings
                    </h1>
                </Container>
                <Container>

                    <Row>
                        <Col>
                            <div className='addListing'>
                                <NavLink to = "/create-listing">
                                    <Button variant ='success' style={{width:'100px'}}>
                                        Add
                                    </Button>
                                </NavLink>
                            </div>
                        </Col>
                    </Row>
                </Container>
                <Container>
                    <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>Book ID</th>
                        <th>Title</th>
                        <th>Author</th>
                        <th>Type</th>
                        <th>Price</th>
                        <th>Status</th>


                        </tr>
                    </thead>
                    <tbody>

                    </tbody>

                </Table>
                </Container>
            </div>

        )
    }
}
