import React from 'react'
import { useState, useEffect} from 'react'
import { Col, Container, Row, Table, Button} from 'react-bootstrap'
import { NavLink } from 'react-router-dom'
import axios from 'axios'
import './MyListings.css'

export const MyListings = () => {
    const[dataLoaded,setDataLoaded] = useState(false);
    const[data, setData] = useState();
    const[bookData, setBookData] = useState();
    const[completeData, setCompleteData] = useState({
        bookId:{},
        title:{},
        author:{},
        type:{},
        price:{},
        sold:{}
    });

    const getTableContents = async () =>
    {
        try
        {
            const response = await axios.get('http://localhost:8082/api/listings/getlistingsbyseller');

            return response.data;
        }
        catch (error)
        {

        }
    }

    const getBookContents= async () =>
    {
        try
        {
            const response = await axios.get('http://localhost:8081/api/books/getallbooks');

            return response.data;
        }
        catch (error)
        {

        }
    }

    useEffect(()=>
    {
        async function fetchData() {
            if (dataLoaded === false)
            {
                const listingsDataFetched = await getTableContents();
                const bookDataFetched = await getBookContents();
                console.log("Listings fetched", listingsDataFetched);
                console.log("Books fetched", bookDataFetched);

                setData(listingsDataFetched);
                setBookData(bookDataFetched);

                const combined = Object.assign({}, data, bookData);
                setCompleteData(combined);
                setCompleteData({
                    bookId:bookData.id,
                    title:bookData.title,
                    author:bookData.author,
                    type:data.type,
                    price:data.price,
                    sold:data.sold
                })

                setDataLoaded(true);
            }
        }
        fetchData();
    }, [dataLoaded, data])

    if(data && bookData){
        const allData = completeData.map(listItem => {
            return (

                <tr>
                <td>{listItem.bookId}</td>
                <td>{listItem.title}</td>
                <td>{listItem.author}</td>
                <td>{listItem.type}</td>
                <td>{listItem.price}</td>
                <td>{listItem.sold}</td>
                </tr>
            )
        })

        return(
            <div>
            <Container>
                <Row>
                    <Col>
                        <div className='addListing'>
                            <NavLink to = "/create-listing">
                            <Button variant ='success' style={{width:'100px'}}>
                                Add
                            </Button>
                            </NavLink>
                        </div>
                    </Col>
                </Row>
            </Container>

            <Container>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>Book ID</th>
                        <th>Title</th>
                        <th>Author</th>
                        <th>Type</th>
                        <th>Price</th>
                        <th>Status</th>


                        </tr>
                    </thead>
                    <tbody>
                        {allData}
                    </tbody>

                </Table>

            </Container>


            </div>
        )
    }
    else{
        return (
            <div>
                <Container>
                    <h1>
                        My Listings
                    </h1>
                </Container>
                <Container>

                    <Row>
                        <Col>
                            <div className='addListing'>
                                <NavLink to = "/create-listing">
                                    <Button variant ='success' style={{width:'100px'}}>
                                        Add
                                    </Button>
                                </NavLink>
                            </div>
                        </Col>
                    </Row>
                </Container>
                <Container>
                    <Table striped bordered hover>
                    <thead>
                        <tr>
                        <th>Book ID</th>
                        <th>Title</th>
                        <th>Author</th>
                        <th>Type</th>
                        <th>Price</th>
                        <th>Status</th>


                        </tr>
                    </thead>
                    <tbody>

                    </tbody>

                </Table>
                </Container>
            </div>

        )
    }
}
